import React, { useEffect, useRef } from "react";
import io from "socket.io-client";
import heatmapjs from "heatmap.js";

const socket = io("http://localhost:5000");  // Updated to port 5000

const Heatmap = () => {
  const heatmapRef = useRef(null);
  const heatmapInstance = useRef(null);

  useEffect(() => {
    heatmapInstance.current = heatmapjs.create({
      container: heatmapRef.current,
      radius: 20,
      maxOpacity: 0.6,
      minOpacity: 0.2,
      blur: 0.75,
    });

    socket.on("updateHeatmap", (data) => {
      heatmapInstance.current.addData({ x: data.x, y: data.y, value: 1 });
    });

    return () => socket.off("updateHeatmap");
  }, []);

  return (
    <div ref={heatmapRef} className="relative w-full h-screen bg-gray-100"></div>
  );
};

export default Heatmap;
