import React, { useEffect, useRef } from "react";
import io from "socket.io-client";
import h337 from "heatmap.js";

const socket = io("http://localhost:5000");

const Heatmap = () => {
  const heatmapRef = useRef(null);
  const heatmapInstance = useRef(null);

  useEffect(() => {
    // Initialize heatmap instance
    heatmapInstance.current = h337.create({
      container: heatmapRef.current,
      radius: 30,
      maxOpacity: 0.6,
      minOpacity: 0.2,
      blur: 0.75,
    });

    // WebSocket connection logs
    socket.on("connect", () => console.log("✅ WebSocket Connected"));
    socket.on("disconnect", () => console.log("❌ WebSocket Disconnected"));

    // Listen for heatmap updates
    socket.on("updateHeatmap", (data) => {
      console.log("🔥 Received Heatmap Data:", data);
      if (data.eventType === "click" && data.x && data.y) {
        heatmapInstance.current.addData({ x: data.x, y: data.y, value: 1 });
      }
    });

    return () => socket.off("updateHeatmap");
  }, []);

  // 🧹 Clear heatmap data
  const handleClearHeatmap = () => {
    heatmapInstance.current.setData({ max: 1, data: [] });
    console.log("🧼 Heatmap cleared!");
  };

  // 📸 Download heatmap as image
  const handleDownloadImage = () => {
    const canvas = heatmapRef.current.querySelector("canvas");
    const link = document.createElement("a");
    link.download = "heatmap_snapshot.png";
    link.href = canvas.toDataURL("image/png");
    link.click();
    console.log("📥 Heatmap image downloaded.");
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">🔥 User Heatmap</h1>
      <div className="flex gap-4 mb-4">
        <button
          onClick={handleClearHeatmap}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          🧹 Clear Heatmap
        </button>
        <button
          onClick={handleDownloadImage}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          📸 Download Image
        </button>
      </div>

      <div
        ref={heatmapRef}
        className="relative w-full h-screen bg-gray-100 rounded shadow"
      ></div>
    </div>
  );
};

export default Heatmap;
