import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000"); // Adjust URL as needed

const AdminDashboard = () => {
  const [userActivities, setUserActivities] = useState([]);

  useEffect(() => {
    socket.on("updateAdminPanel", (data) => {
      console.log("ðŸ“¢ New Activity Received:", data);
      setUserActivities((prev) => [...prev, data]);
    });

    return () => socket.off("updateAdminPanel");
  }, []);

  return (
    <div>
      <h1>Admin Panel - User Activity</h1>
      <ul>
        {userActivities.map((activity, index) => (
          <li key={index}>
            <strong>User ID:</strong> {activity.userId} | 
            <strong>Product ID:</strong> {activity.productId} | 
            <strong>Action:</strong> {activity.action}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
