import React, { useEffect, useState } from "react";
import * as tf from "@tensorflow/tfjs";

const AIAnalytics = () => {
  const [prediction, setPrediction] = useState("Loading...");

  useEffect(() => {
    fetch("http://localhost:5000/api/activity")
      .then((res) => res.json())
      .then((data) => analyzeData(data))
      .catch((err) => console.error("âŒ Error fetching activity data:", err));
  }, []);

  const analyzeData = async (data) => {
    if (data.length === 0) return setPrediction("No user activity data available");

    const clickCounts = data.filter((d) => d.eventType === "click").length;
    const scrollCounts = data.filter((d) => d.eventType === "scroll").length;

    const model = tf.sequential();
    model.add(tf.layers.dense({ units: 1, inputShape: [2] }));
    model.compile({ optimizer: "sgd", loss: "meanSquaredError" });

    const xs = tf.tensor2d([[clickCounts, scrollCounts]]);
    const ys = tf.tensor2d([[clickCounts > scrollCounts ? 1 : 0]]);

    await model.fit(xs, ys, { epochs: 10 });

    const predictionResult = model.predict(tf.tensor2d([[clickCounts, scrollCounts]])).dataSync();
    setPrediction(predictionResult[0] > 0.5 ? "Users are highly interactive!" : "Users are scrolling more than clicking.");
  };

  return (
    <div>
      <h1 className="text-2xl font-bold">ðŸ¤– AI Analytics</h1>
      <p className="text-lg">Prediction: {prediction}</p>
    </div>
  );
};

export default AIAnalytics;
