import React, { useEffect } from "react";
import io from "socket.io-client";
import alokImage from "./alok_sty.png";
import secondImage from "./alok_sty.png";
import thirdImage from "./alok_sty.png";

const socket = io("http://localhost:5000");

const Dashboard = () => {
  useEffect(() => {
    const trackClick = (event, element) => {
      socket.emit("userActivity", {
        eventType: "click",
        element,
        x: event.clientX,
        y: event.clientY,
        timestamp: new Date(),
        userId: "user123",
      });
    };

    const elements = document.querySelectorAll("[data-track]");
    elements.forEach((el) =>
      el.addEventListener("click", (e) => trackClick(e, el.getAttribute("data-track")))
    );

    return () => {
      elements.forEach((el) =>
        el.removeEventListener("click", (e) => trackClick(e, el.getAttribute("data-track")))
      );
    };
  }, []);

  return (
    <div className="p-6 space-y-10">
      <h1 className="text-2xl font-bold">ðŸ›’ Product Page</h1>

      {/* Product 1 */}
      <div className="border rounded-xl p-4 shadow-md w-80">
        <img src={alokImage} alt="Product 1" data-track="Product Image 1" className="w-full cursor-pointer" />
        <button data-track="Add to Cart 1" className="bg-blue-500 text-white px-4 py-2 m-2 w-full">Add to Cart</button>
        <button data-track="Buy Now 1" className="bg-green-500 text-white px-4 py-2 m-2 w-full">Buy Now</button>
      </div>

      {/* Product 2 */}
      <div className="border rounded-xl p-4 shadow-md w-80">
        <img src={secondImage} alt="Product 2" data-track="Product Image 2" className="w-full cursor-pointer" />
        <button data-track="Add to Cart 2" className="bg-blue-500 text-white px-4 py-2 m-2 w-full">Add to Cart</button>
        <button data-track="Buy Now 2" className="bg-green-500 text-white px-4 py-2 m-2 w-full">Buy Now</button>
      </div>

      {/* Product 3 */}
      <div className="border rounded-xl p-4 shadow-md w-80">
        <img src={thirdImage} alt="Product 3" data-track="Product Image 3" className="w-full cursor-pointer" />
        <button data-track="Add to Cart 3" className="bg-blue-500 text-white px-4 py-2 m-2 w-full">Add to Cart</button>
        <button data-track="Buy Now 3" className="bg-green-500 text-white px-4 py-2 m-2 w-full">Buy Now</button>
      </div>
    </div>
  );
};

export default Dashboard;
