import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const Dashboard = () => {
  const [clicks, setClicks] = useState([]);
  const [scrollDepth, setScrollDepth] = useState(0);

  useEffect(() => {
    // Track Mouse Clicks
    const handleClick = (event) => {
      const clickData = {
        eventType: "click",
        x: event.clientX,
        y: event.clientY,
        timestamp: new Date(),
        userId: "user123",
      };
      socket.emit("mouseActivity", clickData);
    };

    // Track Scroll Depth
    const handleScroll = () => {
      const scrollPercent = Math.round(
        (window.scrollY / (document.body.scrollHeight - window.innerHeight)) * 100
      );
      setScrollDepth(scrollPercent);
      socket.emit("mouseActivity", { eventType: "scroll", scrollDepth, userId: "user123" });
    };

    document.addEventListener("click", handleClick);
    window.addEventListener("scroll", handleScroll);

    return () => {
      document.removeEventListener("click", handleClick);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Listen for real-time updates from the backend
  useEffect(() => {
    socket.on("updateHeatmap", (data) => {
      setClicks((prev) => [...prev, data]);
    });

    return () => socket.off("updateHeatmap");
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">üìä User Activity Dashboard</h1>

      {/* Show Scroll Depth */}
      <p className="text-lg mt-2">üìú Scroll Depth: {scrollDepth}%</p>

      {/* Show Click Data */}
      <h2 className="text-xl mt-4">üñ±Ô∏è Click Locations</h2>
      <ul className="mt-2 border border-gray-300 p-4 rounded">
        {clicks.map((click, index) => (
          <li key={index} className="p-2">
            üìç Click at X: {click.x}, Y: {click.y} (Time: {new Date(click.timestamp).toLocaleTimeString()})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
