import React, { useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const Dashboard = () => {
  const userId = "user123"; // Replace this with dynamic user ID from authentication
  const productId = "prod567"; // Replace with dynamic Product ID from the database

  useEffect(() => {
    const trackClick = (event, element) => {
      const eventData = {
        eventType: "click",
        element,
        x: event.clientX,
        y: event.clientY,
        timestamp: new Date(),
        userId,   // âœ… Sending User ID
        productId // âœ… Sending Product ID
      };
      socket.emit("userActivity", eventData);
    };

    const addToCartButton = document.getElementById("add-to-cart");
    const buyNowButton = document.getElementById("buy-now");
    const productImage = document.getElementById("product-image");

    if (addToCartButton) addToCartButton.addEventListener("click", (e) => trackClick(e, "Add to Cart"));
    if (buyNowButton) buyNowButton.addEventListener("click", (e) => trackClick(e, "Buy Now"));
    if (productImage) productImage.addEventListener("click", (e) => trackClick(e, "Product Image"));

    return () => {
      if (addToCartButton) addToCartButton.removeEventListener("click", (e) => trackClick(e, "Add to Cart"));
      if (buyNowButton) buyNowButton.removeEventListener("click", (e) => trackClick(e, "Buy Now"));
      if (productImage) productImage.removeEventListener("click", (e) => trackClick(e, "Product Image"));
    };
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">ðŸ›’ Product Page</h1>
      <img src="product.jpg" alt="Product" id="product-image" className="cursor-pointer w-60" />
      <button id="add-to-cart" className="bg-blue-500 text-white px-4 py-2 m-2">Add to Cart</button>
      <button id="buy-now" className="bg-green-500 text-white px-4 py-2 m-2">Buy Now</button>
    </div>
  );
};

export default Dashboard;
