import React, { useEffect, useRef } from "react";
import io from "socket.io-client";
import h337 from "heatmap.js";

const socket = io("http://localhost:5000");

const Heatmap = () => {
  const heatmapRef = useRef(null);
  const heatmapInstance = useRef(null);

  useEffect(() => {
    if (heatmapRef.current) {
      heatmapInstance.current = h337.create({
        container: heatmapRef.current,
        radius: 30,
        maxOpacity: 0.6,
        minOpacity: 0.2,
        blur: 0.75,
      });
      console.log("âœ… Heatmap initialized");
    }

    socket.on("connect", () => console.log("ðŸŸ¢ WebSocket Connected"));
    socket.on("disconnect", () => console.log("ðŸ”´ WebSocket Disconnected"));

    socket.on("updateHeatmap", (data) => {
      console.log("ðŸ”¥ Received Heatmap Data:", data);
      const { x, y, eventType } = data;

      if (eventType === "click" && typeof x === "number" && typeof y === "number") {
        heatmapInstance.current.addData({ x, y, value: 1 });
        console.log(`ðŸ“ Point added at x: ${x}, y: ${y}`);
      }
    });

    return () => socket.off("updateHeatmap");
  }, []);

  const handleClear = () => {
    heatmapInstance.current?.setData({ max: 1, data: [] });
    console.log("ðŸ§¹ Heatmap cleared");
  };

  const handleDownload = () => {
    const canvas = heatmapRef.current?.querySelector("canvas");
    if (canvas) {
      const link = document.createElement("a");
      link.download = "heatmap_snapshot.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
      console.log("ðŸ“¸ Heatmap downloaded");
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">ðŸ”¥ User Heatmap</h1>
      <div className="flex gap-4 mb-4">
        <button onClick={handleClear} className="bg-red-500 text-white px-4 py-2 rounded">
          ðŸ§¹ Clear Heatmap
        </button>
        <button onClick={handleDownload} className="bg-blue-500 text-white px-4 py-2 rounded">
          ðŸ“¸ Download
        </button>
      </div>
      <div
        ref={heatmapRef}
        className="bg-gray-100 border shadow rounded"
        style={{ width: "100%", height: "90vh", position: "relative" }}
      ></div>
    </div>
  );
};

export default Heatmap;
