import { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const AdminDashboard = () => {
    const [activities, setActivities] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/admin/dashboard")
            .then(res => res.json())
            .then(data => setActivities(data))
            .catch(err => console.error("Error fetching admin data:", err));

        socket.on("updateAdminPanel", (newActivity) => {
            console.log("ðŸ”¥ New activity received:", newActivity);
            setActivities(prev => [newActivity, ...prev]);
        });

        return () => socket.off("updateAdminPanel");
    }, []);

    return (
        <div>
            <h2>Admin Dashboard - User Activities</h2>
            <ul>
                {activities.map((act, index) => (
                    <li key={index}>
                        {act.userId} {act.action} {act.productId} - {new Date(act.timestamp).toLocaleString()}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
