import React, { useEffect, useRef } from "react";
import io from "socket.io-client";
import h337 from "heatmap.js";

const socket = io("http://localhost:5000");

const Heatmap = () => {
  const heatmapRef = useRef(null);
  const heatmapInstance = useRef(null);

  useEffect(() => {
    heatmapInstance.current = h337.create({
      container: heatmapRef.current,
      radius: 40,
      maxOpacity: 0.6,
      minOpacity: 0.1,
      blur: 0.75,
    });

    socket.on("connect", () => console.log("âœ… WebSocket Connected"));
    socket.on("disconnect", () => console.log("âŒ WebSocket Disconnected"));

    socket.on("updateHeatmap", (data) => {
      console.log("ðŸ”¥ Received Heatmap Data:", data);
      if (data.eventType === "click" && data.x && data.y) {
        heatmapInstance.current.addData({
          x: data.x,
          y: data.y,
          value: 1,
        });
      }
    });

    return () => socket.off("updateHeatmap");
  }, []);

  const handleClearHeatmap = () => {
    heatmapInstance.current.setData({ max: 1, data: [] });
    console.log("ðŸ§¹ Heatmap cleared!");
  };

  const handleDownloadImage = () => {
    const canvas = heatmapRef.current.querySelector("canvas");
    const link = document.createElement("a");
    link.download = "heatmap_snapshot.png";
    link.href = canvas.toDataURL("image/png");
    link.click();
  };

  return (
    <div className="p-4">
      <div className="flex gap-4 mb-4">
        <button onClick={handleClearHeatmap} className="bg-red-500 text-white px-4 py-2 rounded">ðŸ§¹ Clear</button>
        <button onClick={handleDownloadImage} className="bg-blue-500 text-white px-4 py-2 rounded">ðŸ“¸ Download</button>
      </div>
      <div
        ref={heatmapRef}
        className="relative w-full h-screen bg-gray-100 rounded shadow"
        style={{ position: "relative", width: "100%", height: "100vh" }}
      />
    </div>
  );
};

export default Heatmap;
