import React, { useEffect, useRef } from "react";
import io from "socket.io-client";
import h337 from "heatmap.js";

// Connect to backend server
const socket = io("http://localhost:5000");

const Heatmap = () => {
  const heatmapRef = useRef(null);
  const heatmapInstance = useRef(null);

  useEffect(() => {
    // Ensure container is available before initializing heatmap
    if (heatmapRef.current) {
      heatmapInstance.current = h337.create({
        container: heatmapRef.current,
        radius: 30,
        maxOpacity: 0.6,
        minOpacity: 0.2,
        blur: 0.75,
      });

      console.log("âœ… Heatmap initialized");
    }

    // Listen to WebSocket events
    socket.on("connect", () => console.log("âœ… WebSocket Connected"));
    socket.on("disconnect", () => console.log("âŒ WebSocket Disconnected"));

    socket.on("updateHeatmap", (data) => {
      console.log("ðŸ”¥ Received Heatmap Data:", data);

      const { x, y, eventType } = data;

      // Validate data before adding
      if (eventType === "click" && typeof x === "number" && typeof y === "number") {
        heatmapInstance.current.addData({ x, y, value: 1 });
        console.log(`ðŸ“ Point added at x: ${x}, y: ${y}`);
      } else {
        console.warn("âš ï¸ Invalid heatmap data:", data);
      }
    });

    // Cleanup
    return () => socket.off("updateHeatmap");
  }, []);

  const handleClearHeatmap = () => {
    if (heatmapInstance.current) {
      heatmapInstance.current.setData({ max: 1, data: [] });
      console.log("ðŸ§¹ Heatmap cleared!");
    }
  };

  const handleDownloadImage = () => {
    const canvas = heatmapRef.current.querySelector("canvas");
    if (canvas) {
      const link = document.createElement("a");
      link.download = "heatmap_snapshot.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
      console.log("ðŸ“¥ Heatmap image downloaded.");
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">ðŸ”¥ User Heatmap</h1>
      <div className="flex gap-4 mb-4">
        <button
          onClick={handleClearHeatmap}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          ðŸ§¹ Clear Heatmap
        </button>
        <button
          onClick={handleDownloadImage}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          ðŸ“¸ Download Image
        </button>
      </div>

      {/* Heatmap container with enforced size */}
      <div
        ref={heatmapRef}
        className="relative bg-gray-100 rounded shadow border"
        style={{
          width: "100%",
          height: "90vh",
          position: "relative",
          overflow: "hidden",
        }}
      ></div>
    </div>
  );
};

export default Heatmap;
