import React, { useEffect, useState } from "react";

const AdminDashboard = () => {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin/logs")
      .then((res) => res.json())
      .then((data) => {
        console.log("üî• Admin Logs:", data);
        setLogs(data);
      })
      .catch((err) => console.error("‚ùå Error fetching logs:", err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">üõ† Admin Dashboard</h1>
      <h2 className="text-xl my-4">üìú Recent User Activity</h2>

      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">Event</th>
            <th className="border p-2">X</th>
            <th className="border p-2">Y</th>
            <th className="border p-2">Scroll Depth</th>
            <th className="border p-2">Timestamp</th>
          </tr>
        </thead>
        <tbody>
          {logs.length > 0 ? (
            logs.map((log, index) => (
              <tr key={index} className="border">
                <td className="p-2">{log.eventType}</td>
                <td className="p-2">{log.x || "-"}</td>
                <td className="p-2">{log.y || "-"}</td>
                <td className="p-2">{log.scrollDepth ? `${log.scrollDepth}%` : "-"}</td>
                <td className="p-2">{new Date(log.timestamp).toLocaleString()}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="p-2 text-center">
                No activity logs found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
